INSERT INTO `tp_articles` VALUES (29, 'Mysql查询', '常见的sql语句编写', 102, '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p><h1 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 2.25em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.2; cursor: text; padding-bottom: 0.3em; border-bottom: 1px solid rgb(238, 238, 238); white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain md-expand\" style=\"box-sizing: border-box;\">mysql表外的查询</span></h1><h2 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 1.75em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.225; cursor: text; padding-bottom: 0.3em; border-bottom: 1px solid rgb(238, 238, 238); white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">联合查询</span></h2><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">联合查询是将2个或者2个以上的字段数量相同的查询结果，‘“纵向堆叠”后合并的结果</span></p><p class=\"md-end-block md-p md-focus\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">select id ,f1,f2 from join1 </span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">union</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">select id2,c2,c2 from join2</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-image md-img-error\" style=\"box-sizing: border-box; min-width: 10px; min-height: 10px; position: relative; word-break: break-all; font-family: monospace; display: inline-block; width: 477px;\"></span></p><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">联合查询语法</span></h3><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">select 查询1</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">union [all 或者 distinct]</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">select 查询2</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">union [all 或者 distinct]</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">说明：</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">所有单个查询结果具有相同列数</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">查询的列类型应一致</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">单个查询的列名可以不同，最终的列名显示第一个查询的列名 </span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">union参数可以带all，distinct 默认distinct</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">order by和limit对结果排序和限制</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">假设：</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">查询1有n1行，m列</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">查询2有n2行，m列</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">联合之后最多有n1+n2行，m列</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><br/></p><h2 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 1.75em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.225; cursor: text; padding-bottom: 0.3em; border-bottom: 1px solid rgb(238, 238, 238); white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">连接查询（join）</span></h2><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">连接查询是将两个查询结果以横向对接的方式合并起来</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\"> 连接查询是将两个查询的每一行，两两横向连接的方式</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-image md-img-error\" style=\"box-sizing: border-box; min-width: 10px; min-height: 10px; position: relative; word-break: break-all; font-family: monospace; display: inline-block; width: 477px;\"></span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-image md-img-error\" style=\"box-sizing: border-box; min-width: 10px; min-height: 10px; position: relative; word-break: break-all; font-family: monospace; display: inline-block; width: 477px;\"></span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">表1有n1行，m1列</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">表2有n2行，m2列</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">1和2连接之后</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">n1*n2行</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">m1+m2列</span></p><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">连接查询的基本形式如下</span></h3><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">select ...from 表1 连接方式 join 表2 [on] 连接条件 where...;</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">可见连接查询只是作为from子句的数据源</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">或者说连接查询是扩大了数据源，从原来的一个表作为数据源，扩大为多个表作为数据源。</span></p><ul class=\"ul-list list-paddingleft-2\" style=\"box-sizing: border-box; margin-top: 0.8em; margin-bottom: 0.8em; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: \"><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">连接查询包括以下不同形式</span></p></li><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">交叉连接，内连接，外连接（左外，右外）</span></p></li></ul><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">### </span></p><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">交叉连接</span></h3><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">语法：</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">from 表1 cross join 表2</span></p><ul class=\"ul-list list-paddingleft-2\" style=\"box-sizing: border-box; margin-top: 0.8em; margin-bottom: 0.8em; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: \"><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">交叉连接可以认为是连接查询的完全版，所有行都被无条件的连接起来了</span></p></li><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">cross可以省略</span></p></li><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">通常应用价值不大</span></p></li></ul><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">内连接</span></h3><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">from 表1 inner join 表2 on 连接条件</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">内连接其实是交叉连接基础上，通过on条件筛选出来的部分数据</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"\" style=\"box-sizing: border-box;\"><strong style=\"box-sizing: border-box;\">内连接是应用最广泛的查询，筛选出有意义的数据</strong></span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">SELECT * FROM product JOIN product_type ON product.protype_id = product_type.protype_id WHERE product.protype_id=1;</span></p><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">外连接</span></h3><h4 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 1.25em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.4; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">左连接</span></h4><ul class=\"ul-list list-paddingleft-2\" style=\"box-sizing: border-box; margin-top: 0.8em; margin-bottom: 0.8em; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: \"><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">from 表1 left join 表2 on</span></p></li><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">左外连接保证左边数据都能取出</span></p></li><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">保证条件，左边表也要取出来</span></p></li><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">SELECT protype_name,price,pinpai,chandi FROM product_type LEFT JOIN product on product_type.protype_id =product.protype_id;</span></p></li></ul><h4 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 1.25em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.4; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">右连接</span></h4><ul class=\"ul-list list-paddingleft-2\" style=\"box-sizing: border-box; margin-top: 0.8em; margin-bottom: 0.8em; padding-left: 30px; position: relative; color: rgb(51, 51, 51); font-family: \"><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">from 表1 right join 表2 on</span></p></li><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">右外连接保证右边数据都能取出</span></p></li><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">保证条件，右边表也要取出来</span></p></li><li><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0px; margin-bottom: 0.5rem; white-space: pre-wrap; position: relative;\"><span class=\"md-plain\" style=\"box-sizing: border-box;\">SELECT user_info.user_id,user_name,order_id ,order_total,order_addr FROM order_info &nbsp;RIGHT JOIN user_info ON &nbsp;user_info.user_id = order_info.user_id;</span></p></li></ul><h2 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 1.75em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.225; cursor: text; padding-bottom: 0.3em; border-bottom: 1px solid rgb(238, 238, 238); white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">自连接</span></h2><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">一个表自己跟自己连接</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">from 表名 as a 连接形式 join 表名 as b on a.xx字段 = b.xx字段</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">说明：</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">自连接还是两个表连接</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">自连接适用于一个表中得值来源于当前表得另一个字段</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-image md-img-error\" style=\"box-sizing: border-box; min-width: 10px; min-height: 10px; position: relative; word-break: break-all; font-family: monospace; display: inline-block; width: 477px;\"></span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">查询每个城市及其所在省份</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-image md-img-error\" style=\"box-sizing: border-box; min-width: 10px; min-height: 10px; position: relative; word-break: break-all; font-family: monospace; display: inline-block; width: 477px;\"></span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-image md-img-error\" style=\"box-sizing: border-box; min-width: 10px; min-height: 10px; position: relative; word-break: break-all; font-family: monospace; display: inline-block; width: 477px;\"></span></p><h2 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 1.75em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.225; cursor: text; padding-bottom: 0.3em; border-bottom: 1px solid rgb(238, 238, 238); white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">子查询</span></h2><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">子查询是指一个正常查询语句中的某个部分（比如select ，from，where部分）又出现了查询的一种查询</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">即嵌套查询</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">select * from xx表 where price &gt;=(一个查询语句)</span></p><h3 class=\"md-end-block md-heading\" style=\"box-sizing: border-box; break-after: avoid-page; break-inside: avoid; font-size: 1.5em; margin-top: 1rem; margin-bottom: 1rem; position: relative; line-height: 1.43; cursor: text; white-space: pre-wrap; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">标量子查询</span></h3><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">含义</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">标量子查询指子查询的结果是“单个值”（一行一列）的查询</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">使用：</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-space: pre-wrap; position: relative; color: rgb(51, 51, 51); font-family: \"><span class=\"md-plain\" style=\"box-sizing: border-box;\">标量子查询通常再where子句中，作为主查询的一个条件判断的数据</span></p><p class=\"md-end-block md-p\" style=\"box-sizing: border-box; line-height: inherit; orphans: 4; margin-top: 0.8em; margin-bottom: 0.8em; white-s